let observer=null;export function onLoad(){const term=document.getElementById("terminal-body");if(!term)return;if(observer){observer.disconnect();observer=null}observer=new MutationObserver(()=>{term.scrollTop=term.scrollHeight});observer.observe(term,{childList:true,subtree:true,characterData:true});term.scrollTop=term.scrollHeight}export function onUpdate(){const term=document.getElementById("terminal-body");if(term){term.scrollTop=term.scrollHeight}}export function onDispose(){if(observer){observer.disconnect();observer=null}}