export function onLoad(){expandAccordionBasedOnOS()}export function onUpdate(){}export function onDispose(){}function expandAccordionBasedOnOS(){const os=window.getOperatingSystem();const linuxNotice=document.getElementById("linuxNotice");if(linuxNotice&&os==="linux"){linuxNotice.style.display="block"}const allCollapses=document.querySelectorAll("#teamsAccordion .accordion-collapse");allCollapses.forEach(collapse=>{collapse.classList.remove("show")});const allButtons=document.querySelectorAll("#teamsAccordion .accordion-button");allButtons.forEach(button=>{button.classList.add("collapsed");button.setAttribute("aria-expanded","false")});let targetCollapseId=null;switch(os){case"windows":targetCollapseId="collapseWindows";break;case"macos":targetCollapseId="collapseMac";break;case"linux":targetCollapseId="collapseLinux";break;default:return}if(targetCollapseId){const targetCollapse=document.getElementById(targetCollapseId);const targetButton=document.querySelector(`[data-bs-target="#${targetCollapseId}"]`);if(targetCollapse&&targetButton){targetCollapse.classList.add("show");targetButton.classList.remove("collapsed");targetButton.setAttribute("aria-expanded","true")}}}